INSERT INTO card_roy (nom, suite, description2, image2) VALUES
("valet", "epee", "Une personne curieuse, observatrice et intelligente, qui valorise la recherche de la vérité et l’apprentissage. Il est souvent perçu comme un débutant sur le plan intellectuel, avide de nouvelles connaissances et toujours en quête d’informations. Son esprit alerte et sa capacité à poser des questions le rendent adaptable", "epee_11_valet.jpg"),
("cavalier", "epee", "Une personne énergique, directe et ambitieuse, qui valorise l'action rapide et l'initiative. Il est souvent perçu comme un guerrier mental, prêt à défendre ses idées et à foncer dans la bataille pour ce qu’il croit juste. Sa volonté d’agir sans crainte en fait une figure de changement.", "epee_12_cava.jpg"),
("reine", "epee", "Une personne claire et honnête, qui valorise l'indépendance et la vérité. Elle est souvent perçue comme directe et perspicace, capable de voir au-delà des apparences. Son esprit aiguisé et sa capacité à dire les choses telles qu’elles sont en font une excellente communicatrice.", "epee_13_reine.jpg"),
("roi", "epee", "Une personne intelligente, autoritaire et juste, qui valorise la vérité et la justice. Il est souvent perçu comme un leader logique, capable de prendre des décisions équilibrées et stratégiques. Son esprit analytique et sa maîtrise des émotions en font un excellent conseiller.", "epee_14_roi.jpg"),
("valet", "baton", "Une personne enthousiaste, curieuse et pleine d’énergie, qui valorise la découverte et l'exploration créative. Il est souvent perçu comme un novice enthousiaste, prêt à embrasser de nouveaux projets et à explorer des opportunités inédites. Son optimisme et son soif d’aventure en font une force créatrice en devenir.", "baton_11_valet.jpg"),
("cavalier", "baton", "Une personne aventureuse, passionnée et impulsive, qui valorise l’action rapide et l'exploration. Il est souvent perçu comme un pionnier, prêt à se lancer dans de nouvelles aventures et à poursuivre ses objectifs avec enthousiasme. Son esprit audacieux et son désir de nouveauté le poussent toujours à l’avant.", "baton_12_cava.jpg"),
("reine", "baton", "Une personne énergique, magnétique et généreuse, qui valorise l'indépendance et la confiance en soi. Elle est souvent perçue comme une femme forte et charismatique, capable d'inspirer et de motiver les autres avec son optimisme et sa vitalité.", "baton_13_reine.jpg"),
("roi", "baton", "Une personne charismatique, confiante et ambitieuse, qui valorise le leadership et l’action. Il est souvent perçu comme un visionnaire, capable d'inspirer les autres par sa passion et son énergie. Son enthousiasme pour les nouveaux projets et sa capacité à prendre des initiatives en font un leader naturel", "baton_14_roi.jpg"),
("valet", "coupe", "Est dans une phase de découverte émotionnelle, où il est peut-être en train d'explorer ses sentiments ou de s'ouvrir à de nouvelles expériences émotionnelles. Il est encouragé à suivre ses inspirations créatives et à exprimer ses émotions de manière authentique.", "coupe_11_valet.jpg"),
("cavalier", "coupe", "Une personne romantique, rêveuse et charmante, qui valorise les idéaux émotionnels et les grands gestes d'amour. Il est souvent perçu comme un porteur de messages émotionnels, capable d’inspirer et d’enchanter son entourage par sa sensibilité et sa quête de beauté dans les relations.", "coupe_12_cava.jpg"),
("reine", "coupe", "Une personne profondément intuitive, aimante et sensible, qui valorise la connexion émotionnelle et la compréhension des autres. Elle est souvent perçue comme une figure maternelle, capable de ressentir les besoins émotionnels des autres et d'apporter soutien et réconfort.", "coupe_13_reine.jpg"),
("roi", "coupe", "Une personne émotionnellement équilibrée, compatissante et compréhensive, qui valorise l'empathie et la sagesse émotionnelle. Il est souvent perçu comme quelqu'un qui maîtrise ses émotions tout en offrant soutien et conseils aux autres. Son calme intérieur et son dévouement aux relations harmonieuses font de lui un guide émotionnel.", "v_14_roi.jpg"),
("valet", "denier", "Une personne curieuse, studieuse et pratique, qui valorise l'apprentissage et l'amélioration de ses compétences matérielles. Il est souvent perçu comme quelqu'un qui débute une nouvelle aventure dans le domaine financier ou professionnel, prêt à investir du temps et de l'énergie pour acquérir des connaissances et bâtir son avenir.", "denier_11_valet.jpg"),
("cavalier", "denier", "Une personne travailleuse, déterminée et méthodique, qui valorise la persévérance et la stabilité dans ses efforts. Il est souvent perçu comme quelqu'un qui avance lentement mais sûrement vers ses objectifs, préférant la constance et la diligence aux gains rapides. Sa patience et son approche méticuleuse en font un bâtisseur de long terme.", "denier_12_cava.jpg"),
("reine", "denier", "Une personne généreuse, maternelle et pragmatique, qui valorise le confort, la sécurité et le bien-être matériel. Elle est souvent perçue comme une figure nourricière, capable de gérer efficacement ses ressources tout en prenant soin des autres. Son approche réaliste et attentionnée en fait une excellente conseillère en matière de finances et de gestion domestique.", "denier_13_reine.jpg"),
("roi", "denier", "Une personne stable, prospère et fiable, qui valorise la sécurité matérielle et la gestion responsable des ressources. Il est souvent perçu comme un leader pragmatique, capable de bâtir des fondations solides et de prendre des décisions financières sages. Sa patience et son sens des responsabilités en font une figure d’autorité en matière de finances et de gestion.", "denier_14_roi.jpg"),
